mapper.select:
select am.menu_id,am.menu_name,am.parent_id,am.menu_url,am.glyphicon,am.version_id FROM fin_admin_menu am,fin_user_menu um WHERE am.menu_id = um.menu_id AND um.user_id=#{userId}
and am.parent_id=0
and am.parent_id!=0

select am.menu_id,am.menu_name,am.parent_id,ifnull(am2.menu_name,'超级管理') parent_name,am.menu_url,am.glyphicon,am.version_id from fin_admin_menu am left join fin_admin_menu am2 on am.parent_id=am2.menu_id
and am.menu_name like '%${menu.menuName}%'
and am.parent_id=#{menu.parentId}
limit #{startIndex},#{pageSize}
